import React from 'react'
import { Container, Carousel } from 'react-bootstrap'
import './Timeline.css'

const Timeline = () => {
    function ControlledCarousel() {
        const [index, setIndex] = useState(0);

        const handleSelect = (selectedIndex, e) => {
            setIndex(selectedIndex);
        };

        return (
            <Carousel activeIndex={index} onSelect={handleSelect}>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="holder.js/800x400?text=First slide&bg=373940"
                        alt="First slide"
                    />
                    <Carousel.Caption>
                        <h3>First slide label</h3>
                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="holder.js/800x400?text=Second slide&bg=282c34"
                        alt="Second slide"
                    />

                    <Carousel.Caption>
                        <h3>Second slide label</h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="holder.js/800x400?text=Third slide&bg=20232a"
                        alt="Third slide"
                    />

                    <Carousel.Caption>
                        <h3>Third slide label</h3>
                        <p>
                            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                </p>
                    </Carousel.Caption>
                </Carousel.Item>
            </Carousel>
        );
    }


    return (
        <>
            <Container fluid className="p-0">
                <div className="roadmap" id="roadmap">
                    <h1 style={{ color: '#fff', textAlign: 'center' }}>short-term timeline</h1>
                    <ControlledCarousel/>
                    <main>
                        <p>
                            Inception of Idea<br />
                            Creation of Base Assets<br />
                            Securing Core Team
                        </p>
                        <p>
                            Discord Launch<br />
                            Twitter Launch<br />
                            Creation of Peripheral Assets<br />
                            Securing Partners<br />
                            White Paper Reveal<br />
                            Art Reveal<br />
                            Giveaways and Promotions<br />
                            Whitelisting Process
                        </p>
                        <p>
                            Whitelist Genesis Auction<br />
                            Genesis Drop<br />
                            Cycle 1 of Merging Start
                        </p>
                        <p>
                            Big Asset Giveaway<br />
                            Community Auction<br />
                            Charity Drive Launch<br />
                            2 Cycle Pause on Merging
                        </p>
                    </main>
                </div>
            </Container>
        </>
    )
}

export default Timeline

