import React, { useState } from 'react'
import { Container, Carousel } from 'react-bootstrap'

const Timeline = () => {
    function ControlledCarousel() {
        const [index, setIndex] = useState(0);

        const handleSelect = (selectedIndex, e) => {
            setIndex(selectedIndex);
        };

        return (
            <Carousel activeIndex={index} onSelect={handleSelect}>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="holder.js/800x400?text=First slide&bg=373940"
                        alt="First slide"
                    />
                    <Carousel.Caption>
                        <h3>First slide label</h3>
                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="holder.js/800x400?text=Second slide&bg=282c34"
                        alt="Second slide"
                    />

                    <Carousel.Caption>
                        <h3>Second slide label</h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src="holder.js/800x400?text=Third slide&bg=20232a"
                        alt="Third slide"
                    />

                    <Carousel.Caption>
                        <h3>Third slide label</h3>
                        <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
                    </Carousel.Caption>
                </Carousel.Item>
            </Carousel>
        );
    }


    return (
        <>
            <Container fluid className="p-0">
                <div >
                    <h1 style={{ color: '#fff', textAlign: 'center' }}>short-term timeline</h1>
                    <ControlledCarousel/>

                </div>
            </Container>
        </>
    )
}

export default Timeline

// Short-term Timeline:

// January 2022
// Inception of Idea
// Creation of Base Assets
// Securing Core Team

// February 2022
// Discord Launch
// Twitter Launch
// Creation of Peripheral Assets
// Securing Partners
// White Paper Reveal
// Art Reveal
// Giveaways and Promotions
// Whitelisting Process

// March 2022
// Whitelist Genesis Auction
// Genesis Drop
// Cycle 1 of Merging Start

// April 2022
// Big Asset Giveaway
// Community Auction
// Charity Drive Launch
// 2 Cycle Pause on Merging
